insert into products (id, name, alias, short_description, full_description, main_image, created_time, updated_time, enabled, in_stock, cost, price, discount_percent, length, width, height, weight, brand_id, category_id, average_rating, review_count) values (7, 'Samsung Galaxy Note II, White 16GB (AT&T)', 'Samsung-Galaxy-Note-II,-White-16GB-(AT&T)', '<div><ul><li>Display: 5.5-inches</li><li>Camera: 8-MP</li><li>Processor Speed: 1.6 GHz</li><li>OS: Android 4.1 (Jelly Bean)</li></ul></div>', '<div>Samsung Galaxy Note II brings a whole new category of smartphones to AT&amp;T. This high-performance device boasts many rich features and is indispensable when it comes to entertainment and productivity thanks to an enhanced S Pen hover experience, intuitive software, and elegant hardware features. Touch, hover, scribble, and speak—the Note II offers multiple tactile ways to interact. Capture and create with the innovative S Pen and preloaded apps like S Memo and S Note. Enjoy multitasking, intuitive interactions, customizable home screens, and more on the latest Google OS Android 4.1, Jelly Bean and the Samsung TouchWiz platform. With the latest HD Super AMOLED Plus technology powering the Note II’s 5.5-inch 720P display, images are bigger, clearer, and brighter than ever allowing you to view color-rich movies, photos, presentations, and gaming. With Note II performance and power are automatic, thanks to Samsung’s Exynos 1.6 Ghz quad-core processor, a long-lasting battery, and blazing fast 4G LTE network speeds.<br></div>', 'Samsung Galaxy Note 2 main.png', TIMESTAMP '2020-09-25 13:34:36.000000', TIMESTAMP '2021-01-12 16:47:09.000000', 1, 1, 513, 599.99, 0, 4, 1.5, 6.2, 0.6, 10, 14, 0, 0);
insert into products (id, name, alias, short_description, full_description, main_image, created_time, updated_time, enabled, in_stock, cost, price, discount_percent, length, width, height, weight, brand_id, category_id, average_rating, review_count) values (8, 'Samsung 860 EVO 500GB 2.5 Inch SATA III Internal SSD (MZ-76E500B/AM)', 'Samsung-860-EVO-500GB-2.5-Inch-SATA-III-Internal-SSD', '<div><ul><li>Brand: Samsung Electronics</li><li>Digital Storage Capacity: 500 GB</li><li>Hardware Interface: SATA 6.0 Gb/s</li><li>Type of product: 2.5 inches</li><li>Write Speed: 520 megabits_per_second</li></ul></div>', '<div><div>Innovative V-Nand Technology: Powered by Samsung V-NAND Technology, the 860 EVO SSD offers optimized performance for everyday computing as well as rendering large-sized 4K videos and 3D data used by the latest applications</div><div>Enhanced read write speeds: Sequential read and write performance levels of up to 550MB/s and 520MB/s, respectively</div><div>Secure encryption: Protect data by selecting security options, including AES 256-bit hardware-based encryption compliant with TCG Opal and IEEE 1670</div><div>Warranty and compatibility: 5-year limited warranty; Windows 8/Windows 7/Windows Server 2003 (32 bit and 64 bit), Vista (SP1 and above), XP (SP2 and above), MAC OSX and Linux</div><div>Included contents: 2.5 inches (7 millimeter) SATA 3 (6 Gb/s) SSD &amp; user manual (All other cables, screws, brackets not included); Reliability (MTBF): 1.5 Million Hours Reliability (MTBF)</div></div>', 'Samsung 500GB SSD main 2.png', TIMESTAMP '2020-09-25 14:00:57.000000', TIMESTAMP '2021-01-12 11:38:09.000000', 1, 1, 49, 109.99, 37, 4.5, 3.5, 0.5, 0.6, 10, 27, 4.5, 2);
