insert into products (id, name, alias, short_description, full_description, main_image, created_time, updated_time, enabled, in_stock, cost, price, discount_percent, length, width, height, weight, brand_id, category_id, average_rating, review_count) values (25, 'GoPro Shorty Mini Extension Pole Tripod', 'GoPro-Shorty-Mini-Extension-Pole-Tripod', '<div><ul><li>The pocket-size shorty (4. 6in, 11. 7cm) is the perfect partner for on-the-go activities, travel and more</li><li>Extend shorty up to 8. 9in (22. 7cm) for group shots, selfies and other hard-to-reach angles</li><li>Prop shorty up on any level surface with its built-in tripod</li><li>Includes 1 Year GoPro Manufacturer</li></ul></div>', '<div>Shorty is a grab-and-go Mini extension pole and tripod. Its compact design makes it ideal to take along for just about any casual activity. Toss it in your pocket on your way out the door, and you’ll have the perfect little partner to position your camera and get the best shot.<br></div>', 'GoPro Tripod main.png', TIMESTAMP '2020-09-26 13:27:56.000000', TIMESTAMP '2020-09-26 13:27:56.000000', 1, 1, 33, 39.99, 7, 7, 2, 1.8, 1.4, 19, 13, 0, 0);
insert into products (id, name, alias, short_description, full_description, main_image, created_time, updated_time, enabled, in_stock, cost, price, discount_percent, length, width, height, weight, brand_id, category_id, average_rating, review_count) values (26, 'Manfrotto Element Aluminum 5-Section Monopod', 'Manfrotto-Element-Aluminum-5-Section-Monopod', '<div><ul><li>Convertible Screw (1/4'' &amp; 3/8) for camera or head connection</li><li>Twist-locking mechanism</li><li>Interchangeable feet (rubber or spiked)</li><li>Ergonomic hand grip w/ wrist strap</li></ul></div>', '<div><div>Manfrotto Element Monopod is the ideal support to carry everywhere, it’s compact when folded and extremely sturdy when opened on field. It comes in two different colors and with twist locking mechanism that make it easy and fast to operate. The ergonomic hand grip allows great transportability and ensure great nimbleness during the shooting experience.</div></div>', 'Manfrotto Monopod main.png', TIMESTAMP '2020-09-26 13:43:02.000000', TIMESTAMP '2020-09-26 13:43:02.000000', 1, 1, 34, 39.99, 0, 6.5, 2.2, 1.7, 1.5, 20, 13, 0, 0);
